                    // Other
                    const {
                        icon,
                        color
                    } = kiss.tools.fileToIcon(fileExtension)
                    preview = `<span id="${file.id}" fieldId="${this.id}" style="color: ${color}" class="fas ${icon} data-type-attachment-icon data-thumbnail"></span>`
