            '-': Symbol('-'),
            '/': Symbol('/'),
            '%': Symbol('%'),
            '*': Symbol('*'),
            '|': Symbol('|'),
            '&': Symbol('&'),
            '^': Symbol('^'),
            '&&': Symbol('&&'),
            '||': Symbol('||'),
            '<': Symbol('<'),
            '<=': Symbol('<='),
            '>': Symbol('>'),
            '>=': Symbol('>='),
            '??': Symbol('??'),
            '==': Symbol('=='),
            '!=': Symbol('!='),
            '!==': Symbol('!=='),
